import React, {useState, useEffect, useRef} from 'react'
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import Message from '../components/Message'
import { getOrderDetails, payOrder } from '../actions/orderActions'
import Loader from '../components/Loader'
import { PayPalButton } from 'react-paypal-button-v2'
import { ORDER_PAY_RESET } from '../constants/orderConstants'
import {InlineShareButtons} from 'sharethis-reactjs'

import { PDFExport, savePDF } from '@progress/kendo-react-pdf'


function OrderScreen({ match }) {

    const pdfExportCompornent = useRef(null)
    const contentArea = useRef(null)

    const handleExportWithComponent = (event) => {
        pdfExportCompornent.current.save()
    }

    const handleExportWithMethod = (event) => {
        savePDF(contentArea.current, { paperSize: "A4" })
    }

    const orderId = match.params.id
    const dispatch = useDispatch()

    const [sdkReady, setSdkReady] = useState(false)


    const orderDetails = useSelector(state => state.orderDetails)
    const { order, error, loading } = orderDetails


    const orderPay = useSelector(state => state.orderPay)
    const { loading:loadingPay, success:successPay } = orderPay

    if(!loading && !error){
        order.itemsPrice = order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0).toFixed(2)
    }

    const styleH1 = {
        fontSize: '15px',
        textAlign: 'center',
        color: 'red'
      };
       
    const invisibleStyle = {
        display: 'none',
      };
   

    const properties = { header: 'Acme' }
    const addPayPalScript = () => {
        const script = document.createElement('script')
        script.type = 'text/javascript'
        script.src = 'https://www.paypal.com/sdk/js?client-id=AfMme_xhIk14WIsgDuJXietSKeQyuv3Gb9XU2Mh9tgfwl-jy2KjSa-24bnzHpcxdNg7uL_Tb7apE2ox8'
        script.async = true
        script.onload = () => {
            setSdkReady(true)
        }
        document.body.appendChild(script)
    }


    useEffect(() => {
        if(!order || successPay || order._id !== Number(orderId)){
            dispatch({type:ORDER_PAY_RESET})
            dispatch(getOrderDetails(orderId))
        }else if(!order.isPaid){
            if (!window.paypal){
                addPayPalScript()
            }else{
                setSdkReady(true)
            }
        }
    }, [dispatch, order, orderId, successPay])

    const successPaymentHandler = (paymentResult) => {
        dispatch(payOrder(orderId, paymentResult))
    }



    return loading ? (
        <Loader />
    ) : error ? (
        <Message variant='danger'>{error}</Message>
    ) :  (
       
        
        <div >
        <PDFExport ref={pdfExportCompornent} paperSize="A4">
       
            <Row>
            
                <Col md={12}>
                    <ListGroup variant='flush'>
                        <ListGroup.Item>
                           <Col style={{marginTop:20}}>
                           <img src='https://i2.wp.com/laksura.com.bd/wp-content/uploads/2021/06/main_logo.png?fit=487%2C180&ssl=1'
                            width="200"
                            height="80"
                            ></img>
                        
                            </Col>
                            <Col>
                            <h4 style={{marginTop:10, marginBottom:10}}><b>Invoice</b></h4>
                            <strong style={{marginRight:5}}>Name:</strong> <h7 >{ order.user.name}</h7><h7 style={{marginLeft:250}}>Invoice & Order Number: {order._id}</h7>
                            <p style={{marginTop:5}}><strong style={{marginRight:5}}>Address: </strong>{ order.shippingAddress.address}<h7 style={{marginLeft:184}}>Order Date: {order.createdAt}</h7></p>
                            <p><strong style={{marginRight:5}}>Mobile Number: </strong>
                                {order.shippingAddress.number}<h7 style={{marginLeft:182}}>Invoice Number: {order._id}</h7></p>
                            
                                             
                            {/* <h3>Order Number: {order._id}</h3> */}
                       
                            
                                {/* <strong>Shipping Address: </strong>
                                {order.shippingAddress.address}, {order.shippingAddress.city},
                                {'  '}
                                {order.shippingAddress.postalCode},
                                {'  '}
                           */}
                                
                                
                                

                            {/* {order.isDelivered ? (
                                <Message variant='success'>Delivered on {order.paidAt}</Message>
                            ) : (
                                <Message variant='secondary'>Your Product is not delivered</Message>
                            )} */}

                            </Col>
                        </ListGroup.Item>


                        <ListGroup.Item>
                            <p>
                                <strong> Payment Method: </strong>
                                {order.paymentMethod}
                            </p>

                            {order.isPaid ? (
                                <Message variant='success'>Paid on {order.paidAt}</Message>
                            ) : (
                                <Message variant='secondary'>Not Paid (Please Pay Soon)</Message>
                            )}
                        </ListGroup.Item>


                        <ListGroup.Item>
                            <h3>Order Items</h3>
                            {order.orderItems.length === 0 ? <Message variant='danger'>
                                You have no order.
                            </Message> : (
                                <ListGroup variant='flush'>
                                    {order.orderItems.map((item, index) => (
                                        <ListGroup.Item key={index}>
                                            <Row>
                                                <Col md={2}>
                                                    <Image src={item.image} alt={item.name} fluid rounded />
                                                </Col>

                                                <Col>
                                                    <Link to={`/product/${item.product}`}>{item.name}</Link>
                                                </Col>

                                                <Col md={4}>
                                                    {item.qty} X <strong className="tk">৳</strong> {item.price} = <strong className="tk">৳</strong> {(item.qty * item.price).toFixed(2)}
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                    ))}

                                </ListGroup>
                            )}
                        </ListGroup.Item>

                    </ListGroup>                   
                </Col>

                <Col>
                    <Card>
                        <ListGroup variant='flush'>
                            <ListGroup.Item>
                                <h2>Order Summary</h2>
                            </ListGroup.Item>


                            <ListGroup.Item>
                                <Row>
                                    <Col>Item: </Col>
                                    <Col>
                                    <strong className="tk">৳</strong> {order.itemsPrice}
                                    </Col>
                                </Row>
                            </ListGroup.Item>


                            <ListGroup.Item>
                                <Row>
                                    <Col>Shipping: </Col>
                                    <Col>
                                    <strong className="tk">৳</strong> {order.shippingPrice}
                                    </Col>
                                </Row>
                            </ListGroup.Item>


                            <ListGroup.Item>
                                <Row>
                                    <Col>Tax: </Col>
                                    <Col>
                                    <strong className="tk">৳</strong> {order.taxPrice}
                                    </Col>
                                </Row>
                            </ListGroup.Item>


                            <ListGroup.Item>
                                <Row>
                                    <Col>Total: </Col>
                                    <Col>
                                    <strong className="tk">৳</strong> {order.totalPrice}
                                    </Col>
                                </Row>
                            </ListGroup.Item>

                            {!order.isPaid && (
                                <ListGroup.Item>
                                    {loadingPay && <Loader/>}
                                    {!sdkReady ? (
                                        <Loader/>
                                    ) : (
                                        <PayPalButton
                                            amount={order.totalPrice}
                                            onSuccess={successPaymentHandler}
                                        />
                                    )}
                                </ListGroup.Item>
                            )}
                            
                            <ListGroup.Item>           
                                <div className='button-area' style={{textAlign:'center'}}>
                                    <Button variant='success' primary={false} onClick={handleExportWithComponent}>Download Invoice</Button>
                                </div>
                            </ListGroup.Item> 
                        </ListGroup>           
                    </Card>
                </Col>
            </Row>
            </PDFExport>
        </div>
    )
}

export default OrderScreen
