import React, { Component } from "react";
import axios from "axios";
import { Form, Button, Row, Col } from 'react-bootstrap'

class App extends Component {
    
  constructor(props) {
    super(props)

    this.state = {
      image: null,
      comment: "",
    };
  }

  
  handleInputChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.files[0]
    });
  };

  handleChange = async (event) => {

    await this.setState({
      [event.target.name]: event.target.value
    });
    console.log(this.state.comment);
  };

  handleFormSubmit = (event) => {
    event.preventDefault();

    let data = new FormData(); 

    data.append("image", this.state.image); 
    data.append("title", this.state.comment);

    axios({
      method: "POST",
      url: "http://127.0.0.1:8000/api/orders/note",
      headers: {
        "Content-Type": "application/json"
      },
      data
    })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };
  

  render() {
    return (
      <div style={{textAlign:'center', marginBottom:300, marginTop:50}} id="other" className="">
        <h3>Upload your payment slip</h3>
        <hr />
        <br />
        <br />
        <br />
        <br />
        

        <form onSubmit={this.handleFormSubmit}>
          {/* <input type="text" name="comment" onChange={this.handleChange} /> */}
          <input type="file" name="image" onChange={this.handleInputChange} />
        
             
          <Button type='submit'  bg="dark">Upload</Button>
        
        </form>
      </div>
    );
  }
}

export default App;
