import React, {useState, useEffect} from 'react'
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import Message from '../components/Message'
import { getOrderDetails, payOrder } from '../actions/orderActions'
import Loader from '../components/Loader'
import { PayPalButton } from 'react-paypal-button-v2'
import { ORDER_PAY_RESET } from '../constants/orderConstants'
import {InlineShareButtons} from 'sharethis-reactjs';



function OrderScreen({ match }) {

    const orderId = match.params.id
    const dispatch = useDispatch()

    const [sdkReady, setSdkReady] = useState(false)


    const orderDetails = useSelector(state => state.orderDetails)
    const { order, error, loading } = orderDetails


    const orderPay = useSelector(state => state.orderPay)
    const { loading:loadingPay, success:successPay } = orderPay

    if(!loading && !error){
        order.itemsPrice = order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0).toFixed(2)
    }

   
    
    const addPayPalScript = () => {
        const script = document.createElement('script')
        script.type = 'text/javascript'
        script.src = 'https://www.paypal.com/sdk/js?client-id=AfMme_xhIk14WIsgDuJXietSKeQyuv3Gb9XU2Mh9tgfwl-jy2KjSa-24bnzHpcxdNg7uL_Tb7apE2ox8'
        script.async = true
        script.onload = () => {
            setSdkReady(true)
        }
        document.body.appendChild(script)
    }


    
    useEffect(() => {
        if(!order || successPay || order._id !== Number(orderId)){
            dispatch({type:ORDER_PAY_RESET})
            dispatch(getOrderDetails(orderId))
        }else if(!order.isPaid){
            if (!window.paypal){
                addPayPalScript()
            }else{
                setSdkReady(true)
            }
        }
    }, [dispatch, order, orderId, successPay])

    const ref = React.createRef();
    const options = {
        orientation: 'landscape',
        unit: 'in',
        format: [4,2]
    };


    const successPaymentHandler = (paymentResult) => {
        dispatch(payOrder(orderId, paymentResult))
    }



    return loading ? (
        <Loader />
    ) : error ? (
        <Message variant='danger'>{error}</Message>
    ) :  (
        
        <div>
            <ReactToPdf targetRef={ref} filename="div-blue.pdf" options={options} x={.5} y={.5} scale={0.8}>
        {({toPdf}) => (
            <button onClick={toPdf}>Generate pdf</button>
        )}
    </ReactToPdf>
            
           
            <h3>Order Number: {order._id}</h3>
            <Row>
                <Col md={8}>
                    <ListGroup variant='flush'>
                        <ListGroup.Item>
                           <hr />
                            <p><strong>Name: </strong> { order.user.name}</p>
                            <p><strong>Email: </strong><a href={`mailto:${order.user.email}`}> { order.user.email}</a></p>
                            
                                <strong>Shipping Address: </strong>
                                {order.shippingAddress.address}, {order.shippingAddress.city},
                                {'  '}
                                {order.shippingAddress.postalCode},
                                {'  '}
                          
                                <p style={{marginTop: 15}}><strong>Mobile Number: </strong>
                                {order.shippingAddress.number}</p>
                                
                            

                            {order.isDelivered ? (
                                <Message variant='success'>Delivered on {order.paidAt}</Message>
                            ) : (
                                <Message variant='secondary'>Your Product is not delivered</Message>
                            )}
                        </ListGroup.Item>


                        <ListGroup.Item>
                            <h3>Payment Method</h3>
                            <p>
                                <strong>Method: </strong>
                                {order.paymentMethod}
                            </p>

                            {order.isPaid ? (
                                <Message variant='success'>Paid on {order.paidAt}</Message>
                            ) : (
                                <Message variant='secondary'>Not Paid (Please Pay Soon)</Message>
                            )}
                        </ListGroup.Item>


                        <ListGroup.Item>
                            <h3>Order Items</h3>
                            {order.orderItems.length === 0 ? <Message variant='danger'>
                                You have no order.
                            </Message> : (
                                <ListGroup variant='flush'>
                                    {order.orderItems.map((item, index) => (
                                        <ListGroup.Item key={index}>
                                            <Row>
                                                <Col md={2}>
                                                    <Image src={item.image} alt={item.name} fluid rounded />
                                                </Col>

                                                <Col>
                                                    <Link to={`/product/${item.product}`}>{item.name}</Link>
                                                </Col>

                                                <Col md={4}>
                                                    {item.qty} X <strong className="tk">৳</strong> {item.price} = <strong className="tk">৳</strong> {(item.qty * item.price).toFixed(2)}
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                    ))}

                                </ListGroup>
                            )}
                        </ListGroup.Item>

                    </ListGroup>                   
                </Col>

                <Col md={4}>
                    <Card>
                        <ListGroup variant='flush'>
                            <ListGroup.Item>
                                <h2>Order Summary</h2>
                            </ListGroup.Item>


                            <ListGroup.Item>
                                <Row>
                                    <Col>Item: </Col>
                                    <Col>
                                    <strong className="tk">৳</strong> {order.itemsPrice}
                                    </Col>
                                </Row>
                            </ListGroup.Item>


                            <ListGroup.Item>
                                <Row>
                                    <Col>Shipping: </Col>
                                    <Col>
                                    <strong className="tk">৳</strong> {order.shippingPrice}
                                    </Col>
                                </Row>
                            </ListGroup.Item>


                            <ListGroup.Item>
                                <Row>
                                    <Col>Tax: </Col>
                                    <Col>
                                    <strong className="tk">৳</strong> {order.taxPrice}
                                    </Col>
                                </Row>
                            </ListGroup.Item>


                            <ListGroup.Item>
                                <Row>
                                    <Col>Total: </Col>
                                    <Col>
                                    <strong className="tk">৳</strong> {order.totalPrice}
                                    </Col>
                                </Row>
                            </ListGroup.Item>


                            {!order.isPaid && (
                                <ListGroup.Item>
                                    {loadingPay && <Loader/>}
                                    {!sdkReady ? (
                                        <Loader/>
                                    ) : (
                                        <PayPalButton
                                            amount={order.totalPrice}
                                            onSuccess={successPaymentHandler}
                                        />
                                    )}
                                </ListGroup.Item>
                            )}

                            <ListGroup.Item>
                                            <br />
                                            <InlineShareButtons
                                                config={{
                                                    alignment: 'center',  // alignment of buttons (left, center, right)
                                                    color: 'white',      // set the color of buttons (social, white)
                                                    enabled: true,        // show/hide buttons (true, false)
                                                    font_size: 16,        // font size for the buttons
                                                    labels: 'cta',        // button labels (cta, counts, null)
                                                    language: 'en',       // which language to use (see LANGUAGES)
                                                    networks: [           // which networks to include (see SHARING NETWORKS)
                                                    'print',     
                                                    ],
                                                    padding: 12,          // padding within buttons (INTEGER)
                                                    radius: 4,            // the corner radius on each button (INTEGER)
                                                    show_total: false,
                                                    size: 35,             // the size of each button (INTEGER)

                                                    // OPTIONAL PARAMETERS
                                                    url: 'https://www.laksura.com.bd', // (defaults to current url)
                                                    image: 'https://bit.ly/2CMhCMC',  // (defaults to og:image or twitter:image)
                                                    description: 'laksura.com is a Global Multifunctional trusted e- commerce site, Which focus on cloud computing digital streaming IoT and artificial intelligence',       // (defaults to og:description or twitter:description)
                                                    title: 'Laksura',            // (defaults to og:title or twitter:title)
                                                    message: 'Please let us know your query',     // (only for email sharing)
                                                    subject: 'Customer Support',  // (only for email sharing)
                                                    username: 'laksura' // (only for twitter sharing)
                                                }}
                                                />
                                            </ListGroup.Item>

                            
                        </ListGroup>

                        
                    </Card>
                </Col>
            </Row>
    
        </div>
    )
}

export default OrderScreen
