import React, { Component } from "react";
import axios from "axios";
import { Form, Button, Row, Col } from 'react-bootstrap'

class App extends Component {
    
  constructor(props) {
    super(props);

    this.state = {
      image: null,
      title: ""
    };
  }

  handleInputChange = async (event) => {
    // event.preventDefault();
    await this.setState({
      [event.target.name]: event.target.files[0]
      //image: event.target.files[0]
      // image: event.target.files[0]
    });
  };

  handleChange = async (event) => {
    // event.preventDefault();
    await this.setState({
      [event.target.name]: event.target.value

      //image: event.target.files[0]
      // image: event.target.files[0]
    });
    console.log(this.state.title);
  };

  handleFormSubmit = (event) => {
    event.preventDefault();

    let data = new FormData(); // creates a new FormData object

    data.append("image", this.state.image); // add your file to form data
    data.append("title", this.state.title);

    axios({
      method: "POST",
      url: "http://127.0.0.1:8000/api/orders/note",
      headers: {
        "Content-Type": "application/json"
      },
      data
    })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };

  render() {
    return (
      <div id="other" className="">
        <p className="mod" style={{ marginTop: "10px" }}>
          Uplaod
        </p>
        <hr></hr>

        <form onSubmit={this.handleFormSubmit}>
          <input type="text" name="title" onChange={this.handleChange} />
          <input type="file" name="image" onChange={this.handleInputChange} />
          <button>Submit</button>
        </form>
      </div>
    );
  }
}

export default App;
