import React, { useState, useEffect } from 'react'
import { Row, Col, Carousel, ListGroup, Tab, Sonnet } from 'react-bootstrap'
import Product from '../components/Product'
import Loader from '../components/Loader'
import Message from '../components/Message'
import { useDispatch, useSelector } from 'react-redux'
import { listProducts } from '../actions/productActions'

function HomeScreen() {
    // const [products, setProducts] = useState([])

    const dispatch = useDispatch()
    const productList = useSelector(state => state.productList)
    const {error, loading, products, p} = productList

    useEffect(() => {
        dispatch(listProducts())

    //     async function fetchProducts() {
    //         const { data } = await axios.get('/api/products/')
    //         setProducts(data)
    //     }

    //     fetchProducts()

    }, [dispatch])

    return (
        <div>
            <Row>
            <Col md={12}>
                <img src='https://static.wixstatic.com/media/9bee35_beebdf8edafb4ff9802088a20d29c262~mv2.gif'
                width="auto"
                height="370"></img>
            </Col>
            </Row>
            <br />
            <h1>Recent Products</h1>
            <hr />
            <br />

            {loading ? <Loader />
                : error ? <Message variant='danger'>{error}</Message>
                    :
                    <Row>
                        {products.map(product => (
                            <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                                <Product product={product} />
                            </Col>
                        ))}
                    </Row> 
                    
                    
                }
                {/* <Row>
              
                     <h1>Motorbike</h1>
                    
                     {products.map(product => (
                            <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                                
                            </Col>
                        ))}
                </Row> */}

                <Row>
                  {products.filter(product => product.category === 'Motorbike').map(product => (
                      <li key={product._id}>{product.name}</li>

                      
                  ))}
                </Row>
            
        </div>
    )
}

export default HomeScreen
