import React, { Component } from "react";
import axios from "axios";
import { Form, Button, Row, Col, Modal } from 'react-bootstrap'

class App extends Component {
    
  constructor(props) {
    super(props);

    this.state = {
      image: null,
      order_number: ""
    };
  }

  handleInputChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.files[0]
    });
  };

  handleChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.value
    });
    console.log(this.state.title);
  };


  handleFormSubmit = (event) => {
    event.preventDefault();

    let data = new FormData(); // creates a new FormData object

    data.append("image", this.state.image); // add your file to form data
    data.append("order_number", this.state.order_number);

    axios({
      method: "POST",
      url: "http://127.0.0.1:8000/api/orders/note",
      headers: {
        "Content-Type": "application/json"
      },
      data
    })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };



  
  render() {
    var modal = document.getElementById("myModal");

// Get the button that opens the modal
var btn = document.getElementById("myBtn");

// Get the <span> element that closes the modal
var span = document.getElementsByClassName("close")[0];

// When the user clicks the button, open the modal 
btn.onclick = function() {
  modal.style.display = "block";
}

// When the user clicks on <span> (x), close the modal
span.onclick = function() {
  modal.style.display = "none";
}

// When the user clicks anywhere outside of the modal, close it
window.onclick = function(event) {
  if (event.target == modal) {
    modal.style.display = "none";
  }
}
    
    return (
      
      
      <div id="other" className="" style={{textAlign:'center', marginTop:80, marginBottom:150}} >
        <h3>Payment verification <i class="fas fa-file-invoice-dollar" style={{color:'green'}}></i></h3>
        <hr></hr>
        <br />
        <br />
        

        <form onSubmit={this.handleFormSubmit} >
        <label for="cheese"><h5><b>Order Number</b></h5></label><br /><br />
          <input type="text" name="order_number" onChange={this.handleChange} placeholder='Enter Order Number' required /><br /><br /><br />
          <label for="cheese"><h5><b>Payment Slip</b></h5></label><br /><br />
          <input type="file" name="image" onChange={this.handleInputChange} style={{marginLeft:100}} required/><br /><br /><br /><br />
          <button className="poka">Upload</button>
        </form>
        <h2>Modal Example</h2>


<button id="myBtn">Open Modal</button>


<div id="myModal" class="modal">

  <div class="modal-content">
    <span class="close">&times;</span>
    <p>Some text in the Modal..</p>
  </div>

</div>

       
        </div>
        
      
    );
  }
}

export default App;
