import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import { Form, Button, Row, Col } from 'react-bootstrap'
import Loader from '../components/Loader'
import Message from '../components/Message'
import { useDispatch, useSelector } from 'react-redux'
import { login } from '../actions/userActions'
import FormContainer from '../components/FormContainer'
import { confirm } from '../actions/orderActions'


function ConfirmOrderScreen({ location, history }) {

    const [comment, setComment] = useState('')

    const [message, setMessage] = useState('')

    const dispatch = useDispatch()

    const redirect = location.search ? location.search.split('=')[1] : '/'

    const orderConfirm = useSelector(state => state.orderConfirm)

    const { error, loading, confirm } = orderConfirm

    useEffect(() => {
        if (userInfo){
            history.push(redirect)
        }            
        }, [history, redirect])


    const submitHandler = (e) => {
        e.preventDefault()
        dispatch(confirm(comment))
    }

    return (
        <FormContainer>
            <h3>Add Comment</h3>
            {message && <Message variant='danger'>{message}</Message>}
            {error && <Message variant='warning'>{error}</Message>}
            {loading && <Loader />}
            <hr />
            <Form onSubmit={submitHandler}>

                <Form.Group controlId='comment'>
                        <Form.Label><strong style={{marginRight: 10}}>Comment </strong><i class="fas fa-user"></i> </Form.Label>
                        <Form.Control
                            required
                            type='comment'
                            // placeholder='Enter Name'
                            value={comment}
                            onChange={(e) => setComment(e.target.value)}
                        >
                        </Form.Control>
                </Form.Group>

             

                    <Button type='submit'  bg="dark">Register</Button>
                    <br />

                </Form>

                
              

        </FormContainer>
    )
}

export default ConfirmOrderScreen
