import React, { Component } from "react";
import axios from "axios";
import { Form, Button, Row, Col, Modal } from 'react-bootstrap'
import swal from 'sweetalert';

class App extends Component {
    
  constructor(props) {
    super(props);

    this.state = {
      password: "",
      token: ""
    };
  }


  handleChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.value
    });
    console.log(this.state.title);
  };

  

  formSubmit = () => {
    swal({
      title: "Success!",
      text: "Your password has been successfully changed. Try to Sign In with your new password",
      icon: "success",
    });
  }


  handleFormSubmit = (event) => {
    event.preventDefault();

    let data = new FormData(); // creates a new FormData object

    data.append("password", this.state.password);
    data.append("token", this.state.token);

    axios({
      method: "POST",
      url: "http://localhost:8000/api/users/password_reset/confirm/",
      headers: {
        "Content-Type": "application/json"
      },
      data
    })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };

 

  
  render() {
    return (

       <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
  <div id="other" className="payment" style={{textAlign:'center', marginTop:30, marginBottom:30}} >
        <h3><i class="fas fa-check-circle"></i> Confirm Passowrd</h3>
        <hr></hr>
        <br />
        <br />
    <form onSubmit={this.handleFormSubmit} >
        <label for="cheese"><h7 style={{marginRight:20}}><b><i class="fas fa-lock"></i> Password </b></h7></label>
          <input class="submissionfield" type="text" name="password" onChange={this.handleChange}  required /><br /><br /><br />
          <label for="cheese"><h7 style={{marginRight:30}}><b><i class="fas fa-sort-numeric-up"></i> Token </b></h7></label>
          <input class="submissionfield" type="text" name="token" onChange={this.handleChange}  required /><br /><br /><br />
          <input type="submit" onClick={this.formSubmit} className="poka" />
        </form>

	</div>
  </div>
      
    );
  }
}

export default App;
