import React from 'react'
import { Spinner } from 'react-bootstrap'
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";


function Loader() {
    return (
        // <Spinner
        //     animation="border" 
        //     role="status"
        //     style = {{
        //         height: '100px',
        //         width: '100px',
        //         margin: 'auto',
        //         display: 'block',
        //     }}
        // >
        <Loader
        type="Puff"
        color="#00BFFF"
        height={100}
        width={100}
        timeout={3000} //3 secs
      >
            <span className='sr-only'>Loading...</span>
            
        </Loader>
    )
}

export default Loader
