import React, { Component } from "react";
import axios from "axios";
import { Form, Button, Row, Col } from 'react-bootstrap'
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

class App extends Component {
    
  constructor(props) {
    super(props);

    this.state = {
      image: null,
      order_number: ""
    };
  }

  handleInputChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.files[0]
    });
  };

  handleChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.value
    });
    console.log(this.state.title);
  };


  handleFormSubmit = (event) => {
    event.preventDefault();

    let data = new FormData(); // creates a new FormData object

    data.append("image", this.state.image); // add your file to form data
    data.append("order_number", this.state.order_number);

    axios({
      method: "POST",
      url: "http://127.0.0.1:8000/api/orders/note",
      headers: {
        "Content-Type": "application/json"
      },
      data
    })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };



  
  render() {
    return (
      <Popup trigger={<button> Trigger</button>} position="bottom">
      <div id="other" className="" style={{textAlign:'center', marginTop:80, marginBottom:150}} >
        <h3>Payment verification <i class="fas fa-file-invoice-dollar" style={{color:'green'}}></i></h3>
        <hr></hr>
        <br />
        <br />
        <form onSubmit={this.handleFormSubmit} >
        <label for="cheese"><h5><b>Order Number</b></h5></label><br /><br />
          <input type="text" name="order_number" onChange={this.handleChange} placeholder='Enter Order Number' required /><br /><br /><br />
          <label for="cheese"><h5><b>Payment Slip</b></h5></label><br /><br />
          <input type="file" name="image" onChange={this.handleInputChange} style={{marginLeft:100}} required/><br /><br /><br /><br />
          <button className="poka">Upload</button>
        </form>
<br />
<br />
<br />

        <button class="open-button" onclick="openForm()">Upload Payment Slip</button>
        <div class="form-popup" id="myForm">
          <form action="/action_page.php" class="form-container">
          <h1>Login</h1>
          <input type="text" placeholder="Enter Email" name="email" required />

          <input type="password" placeholder="Enter Password" name="psw" required /> 

           <button type="submit" class="btn">Login</button>
          <button type="button" class="btn cancel" onclick="closeForm()">Close</button>

          </form>

          
        </div>
        
      </div>
      </Popup>
    );
  }
}

export default App;
