import React, { Component } from "react";
import axios from "axios";
import { Form, Button, Row, Col } from 'react-bootstrap'

class App extends Component {
    
  constructor(props) {
    super(props);

    this.state = {
      image: null,
      order_number: ""
    };
  }

  handleInputChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.files[0]
    });
  };

  handleChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.value
    });
    console.log(this.state.title);
  };

  handleFormSubmit = (event) => {
    event.preventDefault();

    let data = new FormData(); // creates a new FormData object

    data.append("image", this.state.image); // add your file to form data
    data.append("order_number", this.state.order_number);

    axios({
      method: "POST",
      url: "http://127.0.0.1:8000/api/orders/note",
      headers: {
        "Content-Type": "application/json"
      },
      data
    })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };

  render() {
    return (
      <div id="other" className="" style={{textAlign:'center'}}>
        <h3>Payment verification</h3>
        <hr></hr>

        <form onSubmit={this.handleFormSubmit}>
        <label for="cheese">Order Number: </label><br /><br />
          <input  type="text" name="order_number" onChange={this.handleChange} placeholder="Order Information" /><br /><br /><br />
          <input style={{marginLeft:50}} type="file" name="image" onChange={this.handleInputChange} /><br /><br />
          <Button>Submit</Button> 
        </form>
      </div>
    );
  }
}

export default App;
