import React, { Component } from "react";
import axios from "axios";
import { Form, Button, Row, Col, Modal } from 'react-bootstrap'
import swal from 'sweetalert';

class App extends Component {
    
  constructor(props) {
    super(props);

    this.state = {
       email: "",
    };
  }

  handleChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.value
    });
    console.log(this.state.title);
  };

  

  formSubmit = () => {
    swal({
      title: "Success!",
      text: "Your Payment Slip & Order Number Submitted.",
      icon: "success",
    });
  }

  handleFormSubmit = (event) => {
    event.preventDefault();

    let data = new FormData(); // creates a new FormData object

    data.append("email", this.state.email);

    axios({
      method: "POST",
      url: "http://localhost:8000/api/users/password_reset/",
      headers: {
        "Content-Type": "application/json"
      },
      data
    })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };

 

  
  render() {
    return (

//        <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
//   <div id="other" className="payment" style={{textAlign:'center', marginTop:30, marginBottom:30}} >
//         <h3>Payment verification <i class="fas fa-file-invoice-dollar" style={{color:'green'}}></i></h3>
//         <hr></hr>
//         <br />
//         <br />
//     <form onSubmit={this.handleFormSubmit} >
//         <label for="cheese"><h7 style={{marginRight:50}}><b>Order Number </b></h7></label>
//           <input class="submissionfield" type="text" name="email" onChange={this.handleChange} placeholder='Enter Email' required /><br /><br /><br />
//           <input type="submit" onClick={this.formSubmit} className="poka" />
//         </form>

// 	</div>
//   </div>



  <form>
      <h1>Should you have any questions, please do not hesitate to contact me :</h1>
      
    <div class="contentform">
      <div id="sendmessage"> Your message has been sent successfully. Thank you. </div>


      <div class="leftcontact">
            <div class="form-group">
              <p>Surname<span>*</span></p>
              <span class="icon-case"><i class="fa fa-male"></i></span>
                <input type="text" name="nom" id="nom" data-rule="required" data-msg="Vérifiez votre saisie sur les champs : Le champ 'Nom' doit être renseigné."/>
                <div class="validation"></div>
       </div> 

            <div class="form-group">
