import React, { useState, useEffect } from 'react'
import { Row, Col, Carousel, ListGroup, Tab, Sonnet } from 'react-bootstrap'
import Product from '../components/Product'
import Loader from '../components/Loader'
import Message from '../components/Message'
import { useDispatch, useSelector } from 'react-redux'
import { listProducts } from '../actions/productActions'
import InfiniteScroll from 'react-infinite-scroll-component'

function HomeScreen({history, query, pageNumber}) {
    // const [products, setProducts] = useState([])

    const dispatch = useDispatch()
    const productList = useSelector(state => state.productList)
    const {error, loading, p, products} = productList

    let keyword = history.location.search


    console.log(keyword)

    useEffect(() => {
        dispatch(listProducts(keyword))
        params: {  }

    //     async function fetchProducts() {
    //         const { data } = await axios.get('/api/products/')
    //         setProducts(data)
    //     }

    //     fetchProducts()

    }, [dispatch, keyword])

    return (
        <InfiniteScroll
            dataLength={this.products.items.length}
            next={this.fetchMoreData}
            hasMore= {true}
            loader={<h4>Loading...</h4>}
            endMessage={
                <b>You have seen it all</b>
            }
            refreshFunction={this.refresh}
            pullDownToRefresh
            pullDownToRefreshThreshold={50}
            pullDownToRefreshContent={
                <h3 style={{ textAlign: 'center' }}>&#8595; Pull down to refresh</h3>
            }
            releaseToRefreshContent={
                <h3 style={{ textAlign: 'center' }}>&#8593; Release to refresh</h3>
            }
               >
            <Row>
            <Col md={12}>
                <img src='https://static.wixstatic.com/media/9bee35_beebdf8edafb4ff9802088a20d29c262~mv2.gif'
                width="auto"
                height="370"></img>
            </Col>
            </Row>
            <br />
            <h3><b>Recent Products</b></h3>
            <hr />
            <br />

            {loading ? <Loader />
                : error ? <Message variant='danger'>{error}</Message>
                    :
                    <Row className='flex flex-wrap'>
                        {products.map(product => (
                            <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                                <Product product={product} />
                            </Col>
                        ))}
                    </Row>     
                }
                {/* <h1>Motorbikes</h1>
                    <Row>
                        {p.map((product) => (
                            <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                                <Product product={product} />
                            </Col>
                        ))}
                    </Row>  */}










                
                {/* <Row>
              
                     <h1>Motorbike</h1>
                    
                     {products.map(product => (
                            <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                                
                            </Col>
                        ))}
                </Row> */}
        {/* <h1>Motobike</h1>
                <Row>
                  {products.filter(product => product.subcategory === 'Motorbike').map(product => (
                     <Product product={product.name} />

                      
                  ))}
                </Row>
           */}
            
        </InfiniteScroll>
    )
}

export default HomeScreen
