import React, { useState, useEffect } from 'react'
import { Row, Col } from 'react-bootstrap'
import Product from '../components/Product'
import Loader from '../components/Loader'
import Message from '../components/Message'
import { useDispatch, useSelector } from 'react-redux'
import { listProducts } from '../actions/productActions'
import ProductCarousel from '../components/ProductCarousel'

function HomeScreen({history}) {

    const dispatch = useDispatch()
    const productList = useSelector(state => state.productList)
    const {error, loading, products} = productList

    let keyword = history.location.search


    useEffect(() => {
        dispatch(listProducts(keyword))
        params: {  }

    //     async function fetchProducts() {
    //         const { data } = await axios.get('/api/products/')
    //         setProducts(data)
    //     }

    //     fetchProducts()

    }, [dispatch, keyword])

    return (
        <div>
            { !keyword && <ProductCarousel /> }
            <br />
            <h3 ><b><i>Recent Products <i class="fab fa-product-hunt"></i></i></b></h3>
            <hr />
            <br />
            
            {loading ? <Loader />
                : error ? <Message variant='danger'>{error}</Message>
                    :
                    <Row className='flex flex-wrap' >
                        {products.length === 0 && (
                                <Col style={{ textAlign: "center" }}>
                                    <img src='https://cdn.dribbble.com/users/2382015/screenshots/6065978/no_result.gif'
                                    ></img>
                                    
                                </Col>                
                            )}

                        {products.map(product => (
                            <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                                <Product product={product} />
                            </Col>
                        ))}
                    </Row>
                    
                    
                }
                    <br />
                <Row>
                    
                    <Col>
         
                        <h3><b> <i> MotorBike   <i class="fas fa-biking"></i></i></b></h3>
                        <hr />
                        {products.filter(product => product.category === 'Car').map(product => (
                            <li>
                            {product}
                            </li>
                        ))}
                        
                    </Col>
              
                </Row>
        </div>
    )
}

export default HomeScreen
