import React, { useState, useEffect } from 'react'
import { Row, Col, Carousel, ListGroup } from 'react-bootstrap'
import Product from '../components/Product'
import Loader from '../components/Loader'
import Message from '../components/Message'
import { useDispatch, useSelector } from 'react-redux'
import { listProducts } from '../actions/productActions'

function HomeScreen() {
    // const [products, setProducts] = useState([])

    const dispatch = useDispatch()
    const productList = useSelector(state => state.productList)
    const {error, loading, products, p} = productList

    useEffect(() => {
        dispatch(listProducts())

    //     async function fetchProducts() {
    //         const { data } = await axios.get('/api/products/')
    //         setProducts(data)
    //     }

    //     fetchProducts()

    }, [dispatch])

    return (
        <div>
            <Row>
            <Col md={3}>
            <ListGroup>
            <ListGroup.Item>No style</ListGroup.Item>
            <ListGroup.Item variant="primary">Primary</ListGroup.Item>
            <ListGroup.Item variant="secondary">Secondary</ListGroup.Item>
            <ListGroup.Item variant="success">Success</ListGroup.Item>
            <ListGroup.Item variant="danger">Danger</ListGroup.Item>
            <ListGroup.Item variant="warning">Warning</ListGroup.Item>
            <ListGroup.Item variant="info">Info</ListGroup.Item>
            <ListGroup.Item variant="light">Light</ListGroup.Item>
            <ListGroup.Item variant="dark">Dark</ListGroup.Item>
            </ListGroup>
            </Col>

            <Col md={9}>
                <img src='https://newbikeadmin.dwmaster-bikes.co.uk/uploads/offers/image/Suzuki/suzuki-v-strom-650-500-offer-hero.png'
                width="1110"
                height="450"></img>
            </Col>
            </Row>
            <br />
            <h1>Recent Products</h1>
            <hr />
            <br />

            {loading ? <Loader />
                : error ? <Message variant='danger'>{error}</Message>
                    :
                    <Row>
                        {products.map(product => (
                            <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                                <Product product={product} />
                            </Col>
                        ))}
                    </Row>   
                }
                {/* <Row>
              
                     <h1>Motorbike</h1>
                    
                     {products.map(product => (
                            <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                                <Product product={product} />
                            </Col>
                        ))}
                </Row> */}
            
        </div>
    )
}

export default HomeScreen
