import React, { Component } from "react";
import axios from "axios";
import { Form, Button, Row, Col, Modal } from 'react-bootstrap'
import swal from 'sweetalert';

class App extends Component {
    
  constructor(props) {
    super(props);

    this.state = {
      image: null,
      order_number: "",
      account_number: ""
    };
  }

  handleInputChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.files[0]
    });
  };

  handleChange = async (event) => {
    await this.setState({
      [event.target.name]: event.target.value
    });
    console.log(this.state.title);
  };

  

  formSubmit = () => {
    swal({
      title: "Success!",
      text: "Your Payment Slip & Order Number Submitted.",
      icon: "success",
    });
  }

  handleFormSubmit = (event) => {
    event.preventDefault();

    let data = new FormData(); // creates a new FormData object

    data.append("image", this.state.image); // add your file to form data
    data.append("order_number", this.state.order_number);
    data.append("account_number", this.state.account_number);

    axios({
      method: "POST",
      url: "http://127.0.0.1:8000/api/orders/note",
      headers: {
        "Content-Type": "application/json"
      },
      data
    })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };

 

  
  render() {
    return (

       <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
  <div id="other" className="payment" style={{textAlign:'center', marginTop:30, marginBottom:30}} >
        <h3>Payment verification <i class="fas fa-file-invoice-dollar" style={{color:'green'}}></i></h3>
        <hr></hr>
        <br />
        <br />
    <form onSubmit={this.handleFormSubmit} >
        <label for="cheese" ><h7><b>Order Number</b></h7></label><br />
          <input class="submissionfield" type="text" name="order_number" onChange={this.handleChange} required /><br /><br /><br />
          <label for="cheese"><h7><b>Bank Account Number </b></h7></label><br />
          <input class="submissionfield" type="text" name="account_number" onChange={this.handleChange} required /><br /><br /><br />
          <label for="cheese"><h7 ><b >Payment Slip</b></h7></label><br />
          <input type="file" name="image" onChange={this.handleInputChange} style={{marginLeft:100}} required/><br /><br /><br /><br />
          <input type="submit" onClick={this.formSubmit} className="poka" />
        </form>

	</div>
  </div>
      
    );
  }
}

export default App;
